import{_ as t,c as a,a1 as e,o as r}from"./chunks/framework.DdzuDVQG.js";const h=JSON.parse('{"title":"Installing Linux 4 Switch","description":"","frontmatter":{},"headers":[],"relativePath":"extras/installing_linux.md","filePath":"extras/installing_linux.md"}'),n={name:"extras/installing_linux.md"};function o(l,i,s,p,d,u){return r(),a("div",null,i[0]||(i[0]=[e('<h1 id="installing-linux-4-switch" tabindex="-1">Installing Linux 4 Switch <a class="header-anchor" href="#installing-linux-4-switch" aria-label="Permalink to &quot;Installing Linux 4 Switch&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><strong>Have you partitioned your microSD card?</strong></p><p>This guide assumes that you&#39;ve followed the NH-Server guide up until this point, your microSD card should be partitioned accordingly. If you didn&#39;t do so, see <a href="./../user_guide/all/partitioning_sd_syscfw.html">this page</a> of our guide.</p></div><p>Linux 4 Switch is a sister project to Switchroot Android--it uses a similar kernel but provides a variety of Linux distributions.The available distributions at this time are:</p><ul><li>Ubuntu Bionic (maintained by CTCaer, the primary dev of both the L4S kernel and hekate--this is the most stable and supported distro)</li><li>Ubuntu Jammy (maintained by theofficialgman, one of the primary devs of the L4T Megascript installer program)</li><li>Fedora 39 (maintained by azkali, a L4S kernel developer)</li><li>Lakka 5.x (maintained by gavin_darkglider, a L4S kernel developer and Lakka maintainer)</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>Looking for Arch?</strong></p><p>Unfortunately, L4S Arch Linux is deprecated following an xorg ABI change that breaks compatibility with the Tegra210 BSP.</p></div><h2 id="the-installation" tabindex="-1">The installation <a class="header-anchor" href="#the-installation" aria-label="Permalink to &quot;The installation&quot;">​</a></h2><p>To install an L4S distribution, follow the official guide starting from <a href="https://wiki.switchroot.org/wiki/linux/linux-distributions" target="_blank" rel="noreferrer">0. Linux Distributions</a>.</p><ul><li><strong>Note:</strong> You can skip partitioning your microSD card since you partitioned your microSD card during the NH-Server guide. Start from step 4. during the <a href="https://wiki.switchroot.org/wiki/linux/linux-install-update-guide#installation" target="_blank" rel="noreferrer">Installation</a> section.</li></ul><hr><p>This page was made in collaboration with <code>makinbacon21</code> on Discord. See the collapsible section below for the L4S guide maintainers.</p><details class="details custom-block"><summary>L4S Project Staff (Android / Linux)</summary><p>If you&#39;d like, you can donate to the people who made this project possible using these links.</p><ul><li><p>CTCaer (Linux &amp; Low level developer, Hekate maintainer) <a href="https://www.patreon.com/ctcaer" target="_blank" rel="noreferrer">https://www.patreon.com/ctcaer</a></p></li><li><p>Azkali (Linux &amp; Low level developer) <a href="https://www.patreon.com/azkali" target="_blank" rel="noreferrer">https://www.patreon.com/azkali</a></p></li><li><p>gavin_darkglider (Linux &amp; Lakka developer) <a href="https://paypal.me/gavindarkglider" target="_blank" rel="noreferrer">https://paypal.me/gavindarkglider</a></p></li><li><p>ave (Infrastructure &amp; Hosting) <a href="https://patreon.com/aveao" target="_blank" rel="noreferrer">https://patreon.com/aveao</a></p></li></ul></details>',11)]))}const m=t(n,[["render",o]]);export{h as __pageData,m as default};
