import{_ as a,c as o,a1 as t,o as i}from"./chunks/framework.DdzuDVQG.js";const r="/assets/simplemodmanager1.BiM6w8SZ.jpg",n="/assets/simplemodmanager2.D3sswpiV.jpg",u=JSON.parse('{"title":"SimpleModManager","description":"","frontmatter":{},"headers":[],"relativePath":"homebrew/simplemodmanager.md","filePath":"homebrew/simplemodmanager.md"}'),l={name:"homebrew/simplemodmanager.md"};function s(d,e,m,h,c,p){return i(),o("div",null,e[0]||(e[0]=[t('<h1 id="simplemodmanager" tabindex="-1">SimpleModManager <a class="header-anchor" href="#simplemodmanager" aria-label="Permalink to &quot;SimpleModManager&quot;">​</a></h1><p>SimpleModManager is a <a href="./index.html#terminologies">homebrew app</a> that allows easily adding and removing mods to the layeredFS directory from another directory on the SD card.</p><h3 id="common-use-cases-for-simplemodmanager" tabindex="-1">Common use cases for SimpleModManager: <a class="header-anchor" href="#common-use-cases-for-simplemodmanager" aria-label="Permalink to &quot;Common use cases for SimpleModManager:&quot;">​</a></h3><ul><li>Switching between large modpacks</li><li>Enabling and disabling individual mods</li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><strong>What NOT to use SimpleModManager for.</strong></p><p>SimpleModManager should not be used for some specific games like Super Smash Bros. Ultimate and The Legend of Zelda: Breath of the Wild and Tears of the Kingdom. This is due to how these games handle mods differently from others.</p></div><h2 id="installation-requirements" tabindex="-1">Installation requirements: <a class="header-anchor" href="#installation-requirements" aria-label="Permalink to &quot;Installation requirements:&quot;">​</a></h2><ul><li>The latest release of <a href="https://github.com/nadrino/SimpleModManager/releases/latest" target="_blank" rel="noreferrer">SimpleModManager</a> (the <code>SimpleModManager.nro</code> file)</li></ul><h2 id="installation-instructions" tabindex="-1">Installation instructions: <a class="header-anchor" href="#installation-instructions" aria-label="Permalink to &quot;Installation instructions:&quot;">​</a></h2><ol><li>Boot into Hekate and go to <code>Tools</code> &gt; <code>USB Tools</code> &gt; <code>SD Card</code>, then plug your Switch into your PC via USB.</li><li>Your microSD card should now be accessible on your PC, open it.</li><li>Copy the <code>SimpleModManager.nro</code> file the <code>/switch/</code> folder on your microSD card.</li><li>Boot into CFW.</li></ol><h2 id="usage-instructions" tabindex="-1">Usage instructions: <a class="header-anchor" href="#usage-instructions" aria-label="Permalink to &quot;Usage instructions:&quot;">​</a></h2><ol><li>Create a directory named <code>mods/&lt;game name&gt;/&lt;mod name&gt;/</code> on the root of the SD card. <ul><li>What you put for the mod name does not have to align with anything, put whatever you like!</li></ul></li><li>Create the layeredFS directory for your mod that can be found on the <a href="./../extras/game_modding.html">Game Modding</a> page, skipping the <code>atmosphere</code> folder inside the <code>&lt;mod name&gt;</code> folder.</li><li>Launch SimpleModManager via the Homebrew Menu and your games will show up, pressing A on them will show the mods available.</li></ol><p><img src="'+r+'" alt="ExampleSwitchTheme" width="600"><img src="'+n+'" alt="ExampleSwitchTheme" width="600"></p>',12)]))}const f=a(l,[["render",s]]);export{u as __pageData,f as default};
