import{_ as t,c as e,o as i,ae as a}from"./chunks/framework.C2UvtrA0.js";const r="/assets/hekate-partitioning-sys.Dw5YL_oq.png",g=JSON.parse('{"title":"Formatting and/or partitioning the microSD Card","description":"","frontmatter":{},"headers":[],"relativePath":"user_guide/all/partitioning_sd_syscfw.md","filePath":"user_guide/all/partitioning_sd_syscfw.md"}'),n={name:"user_guide/all/partitioning_sd_syscfw.md"};function d(s,o,c,l,u,p){return i(),e("div",null,[...o[0]||(o[0]=[a('<h1 id="formatting-and-or-partitioning-the-microsd-card" tabindex="-1">Formatting and/or partitioning the microSD Card <a class="header-anchor" href="#formatting-and-or-partitioning-the-microsd-card" aria-label="Permalink to &quot;Formatting and/or partitioning the microSD Card&quot;">​</a></h1><h2 id="what-you-need" tabindex="-1">What you need: <a class="header-anchor" href="#what-you-need" aria-label="Permalink to &quot;What you need:&quot;">​</a></h2><ul><li>Your Switch loaded into hekate</li></ul><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p><strong>Partitioning <em>WILL</em> wipe all data on your microSD card!</strong></p><p>In case you missed the warning earlier, your microSD card will be wiped during this page. Go to <code>Tools</code> &gt; <code>USB Tools</code> &gt; <code>SD Card</code>, plug your switch into your PC via USB and back up the contents of your microSD card to your PC if you haven&#39;t done so yet. If you don&#39;t mind redownloading all the games stored on the microSD card and/or all other (potentially important) files getting deleted, you may skip this.</p></div><h2 id="instructions" tabindex="-1">Instructions: <a class="header-anchor" href="#instructions" aria-label="Permalink to &quot;Instructions:&quot;">​</a></h2><ol><li>Navigate to <code>Tools</code> &gt; <code>Partition SD card</code></li><li>Navigate to <code>Next Step</code> at the bottom right, then select <code>Start</code> in the menu that appears. <ul><li>If you wish to install Android and/or Linux later, partition your microSD card here accordingly by moving the sliders you see during this step. We recommend setting the <code>Android (USER)</code> and <code>Linux (EXT4)</code> sliders to at least 16GB.</li><li>For Android; Select <code>Legacy</code> partitioning for Android 10/11 and <code>Dynamic</code> partitioning for Android 13+. Legacy and Dynamic partitioning are <strong>NOT</strong> intercompatible.</li></ul></li><li>Once done, go to hekate&#39;s <code>Home</code> menu and then <code>Tools</code> &gt; <code>USB Tools</code> &gt; <code>SD Card</code> and plug your Switch into your PC via USB.</li><li>Continue with the next step of the guide. <ul><li>Your microSD card should now be accessible on your PC. If not, consult the warning message below.</li></ul></li></ol><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><strong>Your microSD card is not showing up</strong></p><p>If your microSD card isn&#39;t showing up at all, ensure that you&#39;re using a USB cable capable of data transfer and that if you use Windows, Windows has assigned a drive letter to the FAT32 partition of your microSD card. If you still experience errors, join the <a href="https://discord.gg/C29hYvh" target="_blank" rel="noreferrer">NH-Discord server</a> for support.</p></div><p><img src="'+r+'" alt="hekate-partitioning-emu"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><a href="./sd_preparation.html">Continue to SD Preparations</a></p></div>',9)])])}const m=t(n,[["render",d]]);export{g as __pageData,m as default};
